using System;
using System.Collections;
using System.Collections.Generic;
using System.IO;
using System.Text;
using Unity.VisualScripting;
using UnityEditor.PackageManager;
using UnityEngine;
using UnityEngine.Rendering;
using UnityEngine.UI;

public enum typeBook:int
{
    Self_help = 0,
    Children = 1,
    Horror = 2,
    Business = 3,
    Non_fiction = 4,
    Novel = 5,

}
public enum statusBook:int
{
    on_shelf = 0,
    Out_of_stock = 1,
    hide = 2,

}

[System.Serializable]
public class Book 
{
    private string name;
    private int id;
    private int price;
    private string title;
    private string publisher;
    private string author;
    private int stock;
    public Texture2D imgBook;
    private typeBook type;
    private statusBook status;
    public string TypeBook
    {
        get { return type.ToString(); }
        set
        {
            if(value == "Horror") type = typeBook.Horror;
            else if(value == "Self help"||value== "Self_help") type = typeBook.Self_help;
            else if(value == "Business") type = typeBook.Business;
            else if (value == "Non_fiction") type = typeBook.Non_fiction;
            else if (value == "Novel") type = typeBook.Novel;
            else if (value == "Children") type = typeBook.Children;
        }
       
    }
    public int TypeBookInt
    {
        get { return (int)type; }
        set
        {
            type = (typeBook)value;
        }

    }
    public string Status
    {
        get { return status.ToString(); }
        set
        {
            if(value == "on shelf"||value == "on_shelf") status=statusBook.on_shelf;
            else if (value == "hide") status = statusBook.hide;
            else if (value == "Out of stock"||value== "Out_of_stock") status = statusBook.Out_of_stock;
        }
    } public int StatusInt
    {
        get { return (int)status; }
        set
        {
            status = (statusBook)value;
        }

    }


    public string Publisher { get => publisher; set => publisher = value; }
    public string Author { get => author; set => author = value; }
    public int Stock { get => stock; set => stock = value; }
    public int Price { get => price; set => price = value; }
    public string Name { get => name; set => name = value; }
    public int Id { get => id; set => id = value; }
    public string Title { get => title; set => title = value; }

    public Book(string name, int id, int price, string title, string publisher, string author, int stock, string type, string status)
    {
        this.Name = name;
        this.Id = id;
        this.Price = price;
        this.Title = title;
        this.Publisher = publisher;
        this.Author = author;
        this.Stock = stock;

        this.TypeBook = type;
        this.Status = status;
    }


    public void setImg(string imgbase64)
    {
        //imgbase64 = "";
        //Debug.Log(imgbase64);
        byte[] bytes = System.Convert.FromBase64String(imgbase64);
        Texture2D texture = new Texture2D(256, 256);
        Debug.Log(imgbase64.Length);
        // Load the image data from the byte array into the texture
        if (texture.LoadImage(bytes))
        {
            // Apply the texture to the Unity graphics context
            texture.Apply();
            imgBook = texture;
            Debug.Log("hi again");
        }
        else
        {
            Debug.Log("img can loadd again");
        }

    }
}
